mapper.insert:

    SELECT COALESCE(MAX(sn) + 1, 1) FROM brew.brewbbs

INSERT INTO brew.brewbbs (
    sn
    ,title
    ,contents
    ,atch_file_id
    ,inq_cnt
    ,rgtr_id
    ,rgtr_dt
    ,mdfr_id
    ,mdfr_dt
)
VALUES (
        #{sn}
        ,#{title}
        ,#{contents}
        ,#{atchFileId}
        ,0
        ,#{rgtrId}
        ,current_timestamp
        ,#{mdfrId}
        ,current_timestamp
)

mapper.update:
update brew.brewbbs
set
    title = #{title}
    ,contents = #{contents}
    ,atch_file_id = #{atchFileId}
    ,mdfr_id = #{mdfrId}
    ,mdfr_dt = current_timestamp
where 1=1
    and sn = #{sn}

mapper.delete:
delete from brew.brewbbs
where 1=1
    and sn = #{sn}
